// Pega o item vindo do n√≥ anterior
const inputItem = $items()[0];

// Mensagem gerada pela IA
const originalMessage = inputItem.json.output || "";

let correctLink = null;
let pixCode = null;

// 1) Link do Mercado Pago (para de capturar em espa√ßo OU ) ] > )
const LINK_RE = /(https:\/\/www\.mercadopago\.com\.br\/checkout\/v1\/redirect\?pref_id=[^\s)\]>]+)/i;
const linkMatch = originalMessage.match(LINK_RE);
if (linkMatch) correctLink = linkMatch[1];

// 2) C√≥digo PIX: come√ßa em 000201 e para antes de aspas/crases/whitespace
const PIX_RE = /(000201[^\s'"`]+)(?=[\s'"`]|$)/;
const pixMatch = originalMessage.match(PIX_RE);
if (pixMatch) {
  pixCode = pixMatch[1]
    // remove aspas/crases/ap√≥strofos nas pontas
    .replace(/^['"`‚Äô]+|['"`‚Äô]+$/g, "")
    // remove zero-width / BOM se vierem
    .replace(/[\u200B-\u200D\uFEFF]/g, "")
    // remove pontua√ß√£o final acidental
    .replace(/[.,;:]+$/g, "")
    .trim();
}

if (correctLink && pixCode) {
  const nameMatch = originalMessage.match(/Ol√°,\s*([^!.]+)/i);
  const clientName = nameMatch ? nameMatch[1].trim() : "cliente";

  const message1 =
    `Ol√°, ${clientName}! Para renovar seu servi√ßo, voc√™ tem duas op√ß√µes. üòâ\n\n` +
    `*1Ô∏è‚É£ Pagar com Link (Mais R√°pido)*\n` +
    `Clique no link abaixo para pagar com PIX, Cart√£o ou Saldo do Mercado Pago:\n\n` +
    `${correctLink}\n\n---\n\n` +
    `*2Ô∏è‚É£ Pagar com PIX "Copia e Cola"*\n` +
    `Vou enviar o c√≥digo na pr√≥xima mensagem. √â s√≥ copiar e colar no app do seu banco!`;

  const message2 = pixCode;

  return [
    { json: { output: message1, index: 2 } },
    { json: { output: message2, index: 1 } },
  ];
}

// Se n√£o for pagamento, segue normal
return [inputItem];

// Pega a lista de todos os blocos de texto extra√≠dos
const blocos = $input.all()[0].json.blocos_de_texto;

const jogosDoDia = [];

// Varre a lista de blocos de texto
for (let i = 0; i < blocos.length; i++) {
  const blocoAtual = blocos[i];

  // Verifica se o bloco de texto parece ser um jogo (procurando por um hor√°rio como "13h00")
  // A express√£o /\d{1,2}h\d{2}/ encontra textos com esse padr√£o
  if (/\d{1,2}h\d{2}/.test(blocoAtual)) {

    // Se for um jogo, assume que o PR√ìXIMO bloco na lista √© o canal
    // E verifica se o pr√≥ximo bloco realmente existe
    if (i + 1 < blocos.length) {
      const canalDoJogo = blocos[i + 1];

      jogosDoDia.push({
        partida: blocoAtual.trim(),
        // Limpa o texto "Canais: " para deixar s√≥ o nome do canal
        canal: canalDoJogo.replace('Canais: ', '').trim()
      });
    }
  }
}

return jogosDoDia;
