// Pega a lista de todos os blocos de texto extra√≠dos
const blocos = $input.all()[0].json.blocos_de_texto;

const jogosDoDia = [];

// Varre a lista de blocos de texto
for (let i = 0; i < blocos.length; i++) {
  const blocoAtual = blocos[i];

  // Verifica se o bloco de texto parece ser um jogo (procurando por um hor√°rio como "13h00")
  // A express√£o /\d{1,2}h\d{2}/ encontra textos com esse padr√£o
  if (/\d{1,2}h\d{2}/.test(blocoAtual)) {

    // Se for um jogo, assume que o PR√ìXIMO bloco na lista √© o canal
    // E verifica se o pr√≥ximo bloco realmente existe
    if (i + 1 < blocos.length) {
      const canalDoJogo = blocos[i + 1];

      jogosDoDia.push({
        partida: blocoAtual.trim(),
        // Limpa o texto "Canais: " para deixar s√≥ o nome do canal
        canal: canalDoJogo.replace('Canais: ', '').trim()
      });
    }
  }
}

return jogosDoDia;

